import * as types from "../../actionTypes";
import BereichApi from "../../../api/BereichApi";

let actionsFunction = {

  //CRUD METHODS

  // Create bereich
  createBereich: function(bereich) {
    return function(dispatch) {
      return BereichApi
        .createBereich(bereich)
        .then(bereich => {
          dispatch(actionsFunction.createBereichSuccess(bereich));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createBereichSuccess: function(bereich) {
    return { type: types.CREATE_BEREICH_SUCCESS, payload: bereich };
  },


  // Delete bereich
  deleteBereich: function(id) {
    return function(dispatch) {
      return BereichApi
        .deleteBereich(id)
        .then(bereich => {
          dispatch(actionsFunction.deleteBereichSuccess(bereich));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  deleteBereichSuccess: function(bereich) {
    return { type: types.DELETE_BEREICH_SUCCESS, payload: bereich };
  },


  // Get bereich
  loadBereich: function(id) {
    return function(dispatch) {
      return BereichApi
        .getOneBereich(id)
        .then(bereich => {
          dispatch(actionsFunction.loadBereichSuccess(bereich));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadBereichSuccess: function(bereich) {
    return { type: types.GET_BEREICH_SUCCESS, payload: bereich };
  },

  // Load  list
  loadBereichList: function() {
    return function(dispatch) {
      return BereichApi
        .getBereichList()
        .then(list => {
          dispatch(actionsFunction.loadBereichListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadBereichListSuccess: function(list) {
    return { type: types.LIST_BEREICH_SUCCESS, payload: list };
  },

	
  // Save bereich
  saveBereich: function(bereich) {
    return function(dispatch) {
      return BereichApi
        .saveBereich(bereich)
        .then(bereich => {
          dispatch(actionsFunction.saveBereichSuccess(bereich));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  saveBereichSuccess: function(bereich) {
    return { type: types.UPDATE_BEREICH_SUCCESS, payload: bereich };
  },


};

export default actionsFunction;
